mapper.select:
select e.employee_id, e.first_name, e.last_name, e.email, e.phone_number, e.hire_date, e.salary, e.job_id, j.job_title, e.department_id, d.department_name, e1.first_name as manager_name, d.location_id, l.state_province, l.city, l.country_id, c.country_name from employees e left outer join jobs j on e.job_id = j.job_id left outer join departments d on e.department_id = d.department_id left outer join locations l on d.location_id = l.location_id left outer join countries c on l.country_id = c.country_id left outer join employees e1 on e.manager_id = e1.employee_id where 1=1
and (e.first_name like concat('%', #{searchText}, '%') or e.last_name like concat('%', #{searchText}, '%') or d.department_name like concat('%', #{searchText}, '%') )
order by e.employee_id limit #{offset}, #{amount}

mapper.insert:

        insert into employees(
        	first_name,
        	last_name,
        	email,
        	phone_number,
        	hire_date,
        	job_id,
        	salary,
        	commission_pct,
        	manager_id,
        	department_id) 
		values (
			  #{firstName, jdbcType=VARCHAR}
			, #{lastName, jdbcType=VARCHAR}
			, #{email, jdbcType=VARCHAR}
			, #{phoneNumber, jdbcType=VARCHAR}
			, STR_TO_DATE(#{hireDate}, '%Y-%m-%d')
			, #{jobId, jdbcType=VARCHAR}
			, #{salary, jdbcType=NUMERIC }
			, #{commissionPct, jdbcType=NUMERIC }
			, #{managerId, jdbcType=NUMERIC}
			, #{departmentId, jdbcType=NUMERIC}
		)
